import React, {useContext} from 'react';
import CssPost from '../Css/CreatePost.css'
import {Formik, Form, Field, ErrorMessage} from 'formik';
import * as Yup from 'yup'; 
import axios from 'axios';
import {AuthContext} from '../helpers/AuthContext';


function CreatePost() {

  //funzioni per il form

  const {authState} = useContext(AuthContext);

  //Questo mi serve per avere i valori che devo fornire tramite il modulo
  const initialValues ={
    title:'',
    postText:'',
    username:''
  };

  //mi controlla i vari campi
  //string() perche` e` una stringa
  //required() richiesto
  //max e min valore massimo e minimo ammesso
  const validationSchema = Yup.object().shape({
    title:Yup.string().min(3).max(15).required("campo obbligatorio"),
    postText: Yup.string().required(),
    username:Yup.string().min(3).max(15).required()

  });

  //mi invia i dati del modulo
  const onSubmit = (data)=>{
    console.log(data);

    //praticamente questa axios prende un oggetto che passa, ovvero data
    axios.post('http://localhost:3002/posts/aggiungiPost',data).then((response)=>{
        console.log("funziona");
    });
  }

  let navigate = useNavigate();



  return (
    <div className='main flex-container justify-content'>

      {!authState.status ? (<>{}</>):( <div className='container-form '>
        <div className="items-post">
          <Formik initialValues={initialValues} onSubmit={onSubmit} validationSchema={validationSchema}>
            <Form className='input-form'>
              <label >Titolo</label><br/>
              <Field className='field' type="text" id="inputTitle" placeholder="titolo" name="title" />

              <br/>
              <ErrorMessage name="title" component="span"/>


              <label >Testo</label><br/>
              <Field className='field' type="text"  id="inputTesto" placeholder="Testo" name="postText" />

              <br/>
              <ErrorMessage name="postText" component="span"/>


              <label >Username</label><br/>
              <Field className='field' type="text"  id="inputTitle" placeholder="Username" name="username" />

              <br/>
              <ErrorMessage name="username" component="span"/>


            <div>
              <button>Invia</button>
            </div>
            </Form>
          </Formik>

        </div>
      </div>)}

    </div>
  )
}

export default CreatePost
