import React from 'react'
import {useParams} from 'react-router-dom'; //lo importo
import {useEffect, useState} from 'react'
import axios from 'axios';
import post  from '../Css/RecevingPost.css'
import {useNavigate} from 'react-router-dom'

import {Formik, Form, Field, ErrorMessage} from 'formik';

function Post() {

let { id } = useParams();   
const [post, setPost] = useState([])
const [commenti, setCommenti] = useState([]);

useEffect(() => {

    axios.get(`http://localhost:3002/posts/mostraPost/${id}`).then((response)=>{

    //la pagina post deve mostrare un certo tipo di post, quindi prendo l'id dalla url e faccio una get

            console.log(response.data);
            setPost(response.data);
    });

    axios.get(`http://localhost:3002/commenti/mostraCommenti/${id}`).then((response)=>{

            console.log(response.data);
            setCommenti(response.data);
    });

    }, []);




      //Questo mi serve per avere i valori che devo fornire tramite il modulo
  const initialValues ={
    commentBody:'',
    postId:''

  };


  const validationSchema = Yup.object().shape({
    title:Yup.string().min(3).max(15).required("campo obbligatorio"),
    postText: Yup.string().required(),
    username:Yup.string().min(3).max(15).required()

  });

  //mi invia i dati del modulo
  const onSubmit = (data)=>{
    console.log(data);

    //praticamente questa axios prende un oggetto che passa, ovvero data
    axios.post('http://localhost:3002/posts/aggiungiPost',data).then((response)=>{
        console.log("funziona");
    });
  }


  return (
    <div className='flex-container  '>
        <div className='postC justify-content align-center flex-container'>

            <div className='itemsC'>

              <div className='nav-postC flex-container justify-content align-center'>
                  <h1>{post.title}</h1>
              </div>

              <div className='post-centerC flex-container justify-content align-center'>
                  <ul className='listC'>
                  <li>{post.postText}</li>
                  </ul>
              </div>

              <div className='username-postC flex-container '>
                  <h1 className='text'>{post.username}</h1>
              </div>
            </div>
        </div>


        <div className='commenti flex-container jutify-content align-center '>
          {/* Qui metto di inserire un commento */}
              <div className='boxInput flex-container align-center justify-content  '>
                <Formik>
                  <Form>
                    <Field type="textarea" name="commentBody"  id="inputComment" placeholder="Commento"  />
                    <ErrorMessage name="title" component="span"/>

                    <button>Aggiugni</button>
                  </Form>
                </Formik>
              </div>

               <div className='boxComment flex-container justify-content'>
                  <div className='textC'>

                      {commenti.map((comment,key)=>{

                            return ( <p key={key}> {comment.commentBody}</p>);


                          })}
                  </div>
                </div>

        </div>


      </div>

  )
}

export default Post
