  const h1 = ()=>{

    return post;
  }

  useEffect(() => {

    axios.get('http://localhost:3000/posts/mostraPost').then((response)=>{

      console.log(response.data);
      setPost(response.data); //li memorizza dentro lo stato
    });

  }, []);


  return (


    <div className="App">

      <h1> {post.title}</h1>

     {/* {post.map((value,key)=>{

        return (<h1>{value.title}</h1>)

      })} */}

    </div>
  );
}

export default App;
