import './App.css';
import axios from 'axios';
import postCss from './Css/posts.css'
import {useEffect,useState} from 'react';

function App() {

  //STATI
  const [post,setPost] = useState([]);
  // const num = post[0].title;

  //HOOKS
  useEffect(() => {

    axios.get('http://localhost:3000/posts/mostraPost').then((response)=>{

      console.log(response.data);
      setPost(response.data); //li memorizza dentro lo stato
    });

  }, []);

  //FUNZIONI



  return (


    <div className="App">

      {/* Cosi  

       <h1>{post[0].title}</h1>
        <h1>{num}</h1>


      */}


      {post.map((value,key)=>{

        return (

          <div className='container-post  '>
            <div className='items'>
              <div className='nav-post flex-container justify-content align-center'>
                <h1>Post</h1>
              </div>

              <div className='flex-container justify-content align-center'>
                <h1>{value.title}</h1>
                <p>{value.postText}</p>
              </div>

              <div className='username-post flex-container justify-content align-center'>
                <h1>{value.username}</h1>
              </div>
            </div>
          </div>
        )

      })}

    </div>
  );
}

export default App;
