import React from 'react'
import {Formik,Form, Field, ErrorMessage} from "formik";
import * as Yup from "yup";
import axios from 'axios';
import CssLogin from '../Css/Login.css'
import { useState } from 'react';

function Login() {

    const [auth, setAuth]= useState(false);

    const onSubmit = (data)=>{


        console.log(data);
        axios.post("http://localhost:3002/auth/login",data).then((response)=>{

              alert(response.data);
              setAuth(true);

              return(
                  <></>
              );
        });

    }

    const initialValues = {
        username:'',
        password:''
    }

    const validationSchema = Yup.object().shape({
        username: Yup.string().max(15).min(3).required("campo necessario"),
        password: Yup.string().max(10).min(3).required("campo necessario")
    });

  return (
    <div>
          <Formik initialValues={initialValues} onSubmit={onSubmit} validationSchema={validationSchema}>

            <Form className="boxLogin flex-container align-center justify-content">


                <div className='wrapContainerLogin '>
                   <div className='barBoxLogin '>
                   </div>
                    <div className='titleBoxLogin flex-container justify-content'>
                       <h1>Accedi</h1>
                    </div>

                    <Field className='fieldLogin' type="text" id="inputUsername" placeholder="Username" name="username" />

                    <br/>
                    <ErrorMessage name="username" component="span"/>

                    <Field className='fieldLogin' type="password"  id="inputPassword" placeholder="password" name="password" />

                    <br/>
                    <ErrorMessage name="password" component="span"/>

                    <div>
                      <button>Invia 
                        <svg xmlns="http://www.w3.org/2000/svg" width="30" height="30" fill="black" class="bi bi-arrow-right-square-fill" viewBox="0 0 16 16">
                          <path d="M0 14a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V2a2 2 0 0 0-2-2H2a2 2 0 0 0-2 2v12zm4.5-6.5h5.793L8.146 5.354a.5.5 0 1 1 .708-.708l3 3a.5.5 0 0 1 0 .708l-3 3a.5.5 0 0 1-.708-.708L10.293 8.5H4.5a.5.5 0 0 1 0-1z"/>
                        </svg> 
                      </button>  
                      <p  className='recLogin'>Recupera Password oppure Iscriviti</p>    
                    </div>
                </div>
            </Form>
          </Formik>

    </div>
  )
}

export default Login;
