import React from 'react';
import { useState } from 'react';
import {Link} from 'react-router-dom'


//CSS
import cssNavbar from './Css/Navbar.css'

//COMPONENTI
import { AuthContext } from './helpers/AuthContext';

//PAGINE
import './App.css';
import Home from './pages/Home';
import CreatePost from './pages/CreatePost';
import Post from './pages/Post';
import Login from './pages/Login';
import Registrazione from './pages/Registrazione';

import { BrowserRouter ,Route ,Routes} from 'react-router-dom'

function App() {

  const [authState, setAuthState] = useState(false);

  return (


    <div className="App">
      <AuthContext.Provider value={authState, setAuthState }>
        {/* Racchiudo tutta la mia app */}
        <BrowserRouter>


        {/* Quello che faccio qui e` creae un ruotuer che mi indirizza ai componenti*/}
          <Routes>

          <header id="navbar" className='flex-container align-center '>
          <ul id='nav-list' className='flex-container '>
              <li>  <Link to="/">Home</Link> </li>
              <li> <Link to="/Post">Crea un post</Link></li>
            {!authState && (
              <>
              <li><Link to="/Login"> Login</Link></li>
              <li><Link to="/Registrazione">Registrazione</Link></li>
              </>
            )}
          </ul>
        </header>
            <Route path="/" element={<Home/>} />
            <Route path="/Post" element={<CreatePost/>} />
            <Route path="/Post/:id" element={<Post/>} />  
            <Route path="/Login" element={<Login/>} />  
            <Route path="/Registrazione" element={<Registrazione/>} />  

          </Routes>

        </BrowserRouter>
      </AuthContext.Provider>

    </div>
  );
}

export default App;
