import React from 'react'
import axios from 'axios'
import Css from '../Css/posts.css'
import {useEffect,useState} from 'react'
import { useNavigate } from "react-router-dom";

function Home() {

  //STATI
  const [post,setPost] = useState([]);
  const [likedPosts, setLikedPosts] = useState([]);



  let navigate = useNavigate()
  //HOOKS
  useEffect(() => { //per le get ogni volta che fa il refresh pagina

    axios.get('http://localhost:3002/posts/mostraPost',{
      headers: {accessToken: localStorage.getItem("Token")},
    }).then((response)=>{

      console.log(response.data);
      setPost(response.data.listaPost); //li memorizza dentro lo stato
      setLikedPosts(response.data.likedPosts.map((like)=>{
        return like.PostId;
      }));

    });

  }, []);

  const miPiace = (postId) => {
    axios.post("http://localhost:3002/likes/inserisciLike",

        { PostId: postId },
        { headers: { accessToken: localStorage.getItem("Token") } }
      )
      .then((response) => {
        //Dobbiamo aggiornare il post, per farlo dentro il metodo set
        //andiamo a fare una map
        //mi arriva il valore dal db, modifico il mio stato ma prima
        //faccio un controllo e seleziono il post che mi interessa
        //dopodiche` aggiorno il numero di like
        setPost(
          post.map((post)=>{
          if(post.id === postId){
            //ritorniamo il vecchio sato, andando a modificare il campo likes, quindi
            //sommiamo le quantita`
            if(response.data.liked){
              return {...post, Likes:[...post.Likes, 0]};
            }else{ 
              const likesArray =  post.Likes;
              likesArray.pop(); //rimuovo l;ultimo elemento
              return {...post, Likes:likesArray};
            }
          }else{
            return post
          }
         })
        );

        if(likedPosts.includes(postId)){
          setLikedPosts(likedPosts.filter((id)=>{
            return id != postId;
          }));
        }else{
        setLikedPosts([...likedPosts, postId]);
        }

      }); 
  };

  //FUNZIONI

  return (
    <div>

      {post.map((value,key)=>{

            return (

              <div className='container-post  '>
                  <div className='items' >

                    <div className='nav-post flex-container justify-content align-center'>
                        <h1>{value.title}</h1>
                    </div>

                    <div onClick={()=>{navigate(`/Post/${value.id}`);}} className='post-center flex-container justify-content align-center'>
                        <ul className='list'>
                        <li>{value.postText}</li>
                        </ul>
                    </div>

                    <div className='username-post flex-container align-center '>
                        <h1 className='text'>{value.username}</h1>
                        <div className='wrap'>
                          <button onClick={()=>{miPiace(value.id)}} className={likedPosts.includes(value.id) ? "likeBtn" : "unlikeBtn"}  > 
                          <svg xmlns="http://www.w3.org/2000/svg" width="25" height="25" fill="currentColor" class="bi bi-hand-thumbs-up" viewBox="0 0 16 16">
                            <path d="M8.864.046C7.908-.193 7.02.53 6.956 1.466c-.072 1.051-.23 2.016-.428 2.59-.125.36-.479 1.013-1.04 1.639-.557.623-1.282 1.178-2.131 1.41C2.685 7.288 2 7.87 2 8.72v4.001c0 .845.682 1.464 1.448 1.545 1.07.114 1.564.415 2.068.723l.048.03c.272.165.578.348.97.484.397.136.861.217 1.466.217h3.5c.937 0 1.599-.477 1.934-1.064a1.86 1.86 0 0 0 .254-.912c0-.152-.023-.312-.077-.464.201-.263.38-.578.488-.901.11-.33.172-.762.004-1.149.069-.13.12-.269.159-.403.077-.27.113-.568.113-.857 0-.288-.036-.585-.113-.856a2.144 2.144 0 0 0-.138-.362 1.9 1.9 0 0 0 .234-1.734c-.206-.592-.682-1.1-1.2-1.272-.847-.282-1.803-.276-2.516-.211a9.84 9.84 0 0 0-.443.05 9.365 9.365 0 0 0-.062-4.509A1.38 1.38 0 0 0 9.125.111L8.864.046zM11.5 14.721H8c-.51 0-.863-.069-1.14-.164-.281-.097-.506-.228-.776-.393l-.04-.024c-.555-.339-1.198-.731-2.49-.868-.333-.036-.554-.29-.554-.55V8.72c0-.254.226-.543.62-.65 1.095-.3 1.977-.996 2.614-1.708.635-.71 1.064-1.475 1.238-1.978.243-.7.407-1.768.482-2.85.025-.362.36-.594.667-.518l.262.066c.16.04.258.143.288.255a8.34 8.34 0 0 1-.145 4.725.5.5 0 0 0 .595.644l.003-.001.014-.003.058-.014a8.908 8.908 0 0 1 1.036-.157c.663-.06 1.457-.054 2.11.164.175.058.45.3.57.65.107.308.087.67-.266 1.022l-.353.353.353.354c.043.043.105.141.154.315.048.167.075.37.075.581 0 .212-.027.414-.075.582-.05.174-.111.272-.154.315l-.353.353.353.354c.047.047.109.177.005.488a2.224 2.224 0 0 1-.505.805l-.353.353.353.354c.006.005.041.05.041.17a.866.866 0 0 1-.121.416c-.165.288-.503.56-1.066.56z"/>
                          </svg>
                          </button>
                          <label>{value.Likes.length}</label>
                        </div>
                    </div>
                  </div>
              </div>
            )

            })}
    </div>
  )
}

export default Home
