import React from 'react'
import axios from 'axios'
import Css from '../Css/posts.css'
import {useEffect,useState} from 'react'
import { useHistory} from "react-router-dom";

function Home() {

  //STATI
  const [post,setPost] = useState([]);

  let history = useHistory()
  //HOOKS
  useEffect(() => {

    axios.get('http://localhost:3002/posts/mostraPost').then((response)=>{

      console.log(response.data);
      setPost(response.data); //li memorizza dentro lo stato
    });

  }, []);

  //FUNZIONI

  return (
    <div>

      {post.map((value,key)=>{

            return (

              <div className='container-post  '>
                  <div className='items' 
                   onClick={()=>{
                    history.push(`/Post/${value.id}`);
                  }}>

                    <div className='nav-post flex-container justify-content align-center'>
                        <h1>{value.title}</h1>
                    </div>

                    <div className='post-center flex-container justify-content align-center'>
                        <ul className='list'>
                        <li>{value.postText}</li>
                        </ul>
                    </div>

                    <div className='username-post flex-container align-center '>
                        <h1 className='text'>{value.username}</h1>
                    </div>
                  </div>
              </div>
            )

            })}
    </div>
  )
}

export default Home
