                <ul className='list'>
                <li>{post.postText}</li>
                </ul>
            </div>

            <div className='username-post flex-container '>
                <h1 className='text'>{post.username}</h1>
            </div>
          </div>
      </div>

      <div className='commenti'>



      </div>
      </>

  )
}

export default Post
