import React from 'react'
import {Formik,Form, Field, ErrorMessage} from "formik";
import * as Yup from "yup";
import axios from 'axios';
import CssRegistrazione from '../Css/Registrazione.css'

function Registrazione() {

    const onSubmit = (data)=>{
        axios.post("https://localhost:3002/utenti/inserisciUtente",data).then((response)=>{


        });

    }

    const initialValues = {
        username:"",
        password:""
    }

    const validationSchema = {
        username: Yup.string().max(15).min(3).required("campo necessario"),
        password: Yup.string().max(10).min(3).required("campo necessario")
    }

  return (
    <div>

<Formik initialValues={initialValues} onSubmit={onSubmit} validationSchema={validationSchema}>
            <Form className='input-form'>
              <label> Username </label><br/>
              <Field className='field' type="text" id="inputTitle" placeholder="titolo" name="title" />

              <br/>
              <ErrorMessage name="title" component="span"/>


              <label >Testo</label><br/>
              <Field className='field' type="text"  id="inputTitle" placeholder="Testo" name="postText" />

              <br/>
              <ErrorMessage name="postText" component="span"/>


              <label >Username</label><br/>
              <Field className='field' type="text"  id="inputTitle" placeholder="Username" name="username" />

              <br/>
              <ErrorMessage name="username" component="span"/>


            <div>
              <button>Invia</button>
            </div>
            </Form>
          </Formik>








    </div>
  )
}

export default Registrazione
