import React from 'react'
import axios from 'axios'
import {useEffect,useState} from 'react'

function Home() {

  //STATI
  const [post,setPost] = useState([]);
  // const num = post[0].title;

  //HOOKS
  useEffect(() => {

    axios.get('http://localhost:3000/posts/mostraPost').then((response)=>{

      console.log(response.data);
      setPost(response.data); //li memorizza dentro lo stato
    });

  }, []);

  //FUNZIONI



  return (
    <div>

      {post.map((value,key)=>{

            return (

            <div className='container-post  '>
                <div className='items'>

                <div className='nav-post flex-container justify-content align-center'>
                    <h1>{value.title}</h1>
                </div>

                <div className='post-center flex-container justify-content align-center'>
                    <ul className='list'>
                    <li>{value.postText}</li>
                    </ul>
                </div>

                <div className='username-post flex-container '>
                    <h1 className='text'>{value.username}</h1>
                </div>
                </div>
            </div>
            )

            })}
    </div>
  )
}

export default Home
