import React from 'react';
import { useState, useEffect } from 'react';
import {Link} from 'react-router-dom'
import axios from 'axios';

//CSS
import cssNavbar from './Css/Navbar.css'

//COMPONENTI
import { AuthContext } from './helpers/AuthContext';

//PAGINE
import './App.css';
import Home from './pages/Home';
import CreatePost from './pages/CreatePost';
import Post from './pages/Post';
import Login from './pages/Login';
import Registrazione from './pages/Registrazione';

import { BrowserRouter ,Route ,Routes} from 'react-router-dom'

function App() {

  const [authState, setAuthState] = useState({username:'', id:'', status:false});
  const [listOfPosts, setListOfPosts] = useState([]);

  const logout = () =>{

    localStorage.removeItem("Token");
    setAuthState({username:"",id:0, status:false});
  }


  const miPiace = (postId) => {
    axios
      .post(
        "http://localhost:3002/like",
        { PostId: postId },
        { headers: { accessToken: localStorage.getItem("accessToken") } }
      )
      .then((response) => {
        setListOfPosts(
          listOfPosts.map((post) => {
            if (post.id === postId) {
              if (response.data.liked) {
                return { ...post, Likes: [...post.Likes, 0] };
              } else {
                const likesArray = post.Likes;
                likesArray.pop();
                return { ...post, Likes: likesArray };
              }
            } else {
              return post;
            }
          })
        );
      });
  };

  return (

    <AuthContext.Provider value={ {authState, setAuthState} }>
      <div className="App">
        {/* Racchiudo tutta la mia app */}
        <BrowserRouter>

          <header id="navbar" className='flex-container align-center justify-content'>
          <ul id='nav-list' className='flex-container align-center justify-content '>
              <li>  <Link to="/">Home</Link> </li>
              <li> <Link to="/Post">Crea un post</Link></li>


              {!authState.status ? (
              <>
                <Link to="/login"> Login</Link>
                <Link to="/Registrazione"> Registration</Link>

              </>
            ) : (   <div className='center flex-container align-center '>
                      <h1>{authState.username}</h1>
                      <button onClick={logout}>Logout</button>
                    </div>
                  )}
          </ul>


        </header>

          {/* Quello che faccio qui e` creae un ruotuer che mi indirizza ai componenti*/}
            <Routes>
              <Route path="/" element={<Home/>} />
              <Route path="/Post" element={<CreatePost/>} />
              <Route path="/Post/:id" element={<Post/>} />  
              <Route path="/Login" element={<Login/>} />  
              <Route path="/Registrazione" element={<Registrazione/>} />  

            </Routes>

        </BrowserRouter>


    </div>
    </AuthContext.Provider>

  );
}

export default App;
