import React from 'react'
import axios from 'axios'
import Css from '../Css/posts.css'
import {useEffect,useState} from 'react'
import { useNavigate } from "react-router-dom";

function Home() {

  //STATI
  const [post,setPost] = useState([]);

  let navigate = useNavigate()
  //HOOKS
  useEffect(() => {

    axios.get('http://localhost:3002/posts/mostraPost').then((response)=>{

      console.log(response.data);
      setPost(response.data); //li memorizza dentro lo stato
    });

  }, []);

  //FUNZIONI

  return (
    <div>

        <div className='container-post  '>
            <div className='items' onClick={()=>{
              navigate(`/Post/${post.id}`);
            }}>

              <div className='nav-post flex-container justify-content align-center'>
                  <h1>{post.title}</h1>
              </div>

              <div className='post-center flex-container justify-content align-center'>
                  <ul className='list'>
                  <li>{post.postText}</li>
                  </ul>
              </div>

              <div className='username-post flex-container '>
                  <h1 className='text'>{post.username}</h1>
              </div>
            </div>
        </div>

    </div>
  )
}

export default Home
