import React from 'react'
import {Formik,Form, Field, ErrorMessage} from "formik";
import * as Yup from "yup";
import axios from 'axios';
import CssRegistrazione from '../Css/Registrazione.css'
import { FontAwesomeIcon } from '@fortawesome/fontawesome-svg-core'

function Registrazione() {

    const onSubmit = (data)=>{
        axios.post("https://localhost:3002/utenti/inserisciUtente",data).then((response)=>{
        });

    }

    const initialValues = {
        username:"",
        password:""
    }

    const validationSchema = {
        username: Yup.string().max(15).min(3).required("campo necessario"),
        password: Yup.string().max(10).min(3).required("campo necessario")
    }

  return (
    <div>
          <Formik  initialValues={initialValues} onSubmit={onSubmit} validationSchema={validationSchema}>

            <Form className="boxRegistrazione flex-container align-center justify-content">


                <div className='wrapContainer '>
                   <div className='barBox'>
                   </div>
                    <div className='titleBox flex-container justify-content align-center'>
                       <h1>Registrati</h1>
                    </div>


                    <Field className='field' type="text" id="inputUsername" placeholder="Username" name="username" />

                    <br/>
                    <ErrorMessage name="username" component="span"/>

                    <Field className='field' type="text"  id="inputPassword" placeholder="Testo" name="password" />

                    <br/>
                    <ErrorMessage name="password" component="span"/>

                    <div>
                    <button>Registrati 
                      <FontAwesomeIcon icon="fa-solid fa-arrow-right-to-bracket" /> 
                    </button>
                    </div>
                </div>
            </Form>
          </Formik>

    </div>
  )
}

export default Registrazione;
