import React from 'react'
import {useParams} from 'react-router-dom'; //lo importo
import {useEffect, useState} from 'react'
import axios from 'axios';
import post  from '../Css/posts.css'
import {useNavigate} from 'react-router-dom'

function Post() {

let { id } = useParams();   
const [post, setPost] = useState([])
let navigate = useNavigate();

useEffect(() => {

    axios.get(`http://localhost:3002/posts/mostraPost/${id}`).then((response)=>{

    //la pagina post deve mostrare un certo tipo di post, quindi prendo l'id dalla url e faccio una get

            console.log(response.data);
            setPost(response.data);
    });

    }, []);

  return (

      <div className='container-post  '>
          <div className='items'>

            <div className='nav-post flex-container justify-content align-center'>
                <h1>{post.title}</h1>
            </div>

            <div className='post-center flex-container justify-content align-center'>
                <ul className='list'>
                <li>{post.postText}</li>
                </ul>
            </div>

            <div className='username-post flex-container '>
                <h1 className='text'>{post.username}</h1>
            </div>
          </div>
      </div>

  )
}

export default Post
